<template>
  <a-modal title="选择商协会" width="70%" @ok="handleOkSelect()">
    <PlusTable
      :row-selection="{ onChange: onSelectChange, selectedRowKeys: selectedTableRow, preserveSelectedRowKeys: true }"
      :columns="columns"
      :data-source="tableData"
      :scroll="{ x: 'max-content' }"
      row-key="id"
      :pagination="pagination"
      :loading="loading"
      @change="handleTableChange"
      :request="handlerSearch"
      class="p0"
    >
      <template #bodyCell="{ column, record }">
        <template v-if="column.dataIndex == 'systemLogo'">
          <a-image
            class="chamber-logo"
            :src="record.systemLogo"
            fallback="data:image/png;base64,"
          />
        </template>
      </template>
    </PlusTable>
  </a-modal>
</template>
<script>
import { defineAsyncComponent, ref, watch } from 'vue'
import { getChamberData, addFriendLinks } from '@/api/home/friendLink'
import { useAntTable } from '@/use/useAntTable'
import { message } from 'ant-design-vue'

export default {
  components: {
    PlusTable: defineAsyncComponent(() => import('@/components/plusTable/PlusTable.vue'))
  },
  setup(props, { emit }) {
    const tableData = ref([])
    const tableFilter = ref({
      name: ''
    })
    let queryFirstPage = 0

    watch(
      () => tableFilter.value.name,
      () => {
        console.log('tableFilter')

        queryFirstPage = 1
      },
      { immediate: true }
    )
    const { fetchTableData, pagination, handleTableChange, loading } = useAntTable({
      async fetchFn({ current, pageSize }) {
        const { data } = await getChamberData({
          chamberName: tableFilter.value.name,
          pageNum: queryFirstPage || current,
          pageSize
        })
        queryFirstPage = 0
        tableData.value = data.list || []
        return { total: data.totalRows || 0 }
      }
    })
    const handlerSearch = value => {
      tableFilter.value = value
      setTimeout(() => {
        fetchTableData()
      }, 500)
    }
    fetchTableData()

    const selectedTableRow = ref([])
    const chooseTableData = ref([])
    const handleOkSelect = async () => {
      const ckeyList = []
      if (!chooseTableData.value.length) return message.warn('请勾选供需')

      chooseTableData.value.forEach(element => {
        ckeyList.push(element.ckey)
      })
      await addFriendLinks({ ckeyList })
      fetchTableData()
      emit('fetchFn')
    }

    // 选中表格
    const onSelectChange = (index, row) => {
      selectedTableRow.value = index
      chooseTableData.value = row.filter(item => item !== undefined)
    }
    return {
      columns: [
        { title: '商协会logo', dataIndex: 'systemLogo', align: 'center', width: 200 },
        {
          title: '商协会名称',
          dataIndex: 'name',
          align: 'center',
          width: 200,
          filter: true,
          valueType: 'text',
          formItemProps: {
            placeholder: '请输入关键词'
          },
          span: 12
        }
      ],
      tableData,
      fetchTableData,
      pagination,
      handleTableChange,
      loading,
      handlerSearch,
      handleOkSelect,
      onSelectChange,
      selectedTableRow
    }
  }
}
</script>
